2;//s0ä¸ºç¬¦åè¦æ±çæ°ãs1ä¸ºç¬¦åè¦æ±çæ°1æ¬¡æ¹ä¹åï¼s2ä¸ºç¬¦åè¦æ±çæ°ç2æ¬¡æ¹ä¹åã\n}f[n][10][7][7];//f[i][j][k][u]è¡¨ç¤ºæ»å±æiä½æ°ä¸æé«ä½æ¯jï¼è¯¥æ°å¼æ¨¡7ä¸ºkï¼åä½æ°æ°å­ä¹åæ¨¡7ä¸ºuçæææ°çs0,s1,s2.\n//è¿è¡åå§åã\nint t;//æµè¯æ°ã\nll l,r;\nll power7[n],power9[n];//power7[i]å­å¨10^iä½7çä½æ°ï¼power9[i]å­å¨10^iä½pçä½æ°ã\nll mod(ll x,ll y){\n    return (x%y+y)%y;\n}\nvoid init(){\n    //ç¡®å®åå§å¼ï¼ä½æ°ä¸º1çæåµã\n    for(int j=0;j<10;j++){\n        if(j==7)continue;\n        //æ ¹æ®æ§è´¨æé¤ä¸ç¬¦åè¦æ±çã\n        f &v=f[1][j][j%7][j%7];//è¿éç¨å¼ç¨åå°ä»£ç éã\n        v.s0++;\n        v.s1+=j;\n        v.s2+=j*j;\n    }\n    ll power = 10;//è¾å©ä½ç¨ï¼è¡¨ç¤º10çi-1æ¬¡æ¹ã\n    for(int i=2;i<n;i++,power*=10){\n        for(int j=0;j<10;j++){\n            if(j==7)continue;//æé¤ä¸ç¬¦åè¦æ±çæ°ã\n            for(int k=0;k<7;k++){\n                for(int u=0;u<7;u++){\n                    for(int q=0;q<10;q++){\n                        //æä¸¾i-1çæé«ä½ã\n                        if(q==7)continue;\n                        f &x=f[i][j][k][u],y=f[i-1][q][mod(k-j*(power%7),7)][mod(u-j,7)];\n                        //s0,s1,s2é½æ¯éè¿å¬å¼å°±ç®å¾å°ã\n                        x.s0=mod(x.s0+y.s0,p);\n                        x.s1=mod(x.s1+1ll*j%p*(power%p)%p*y.s0%p+y.s1,p);\n                        x.s2=mod(x.s2+\n                            1ll*j%p*y.s0%p*(power%p)%p*j%p*(power%p)%p+\n                            1ll*y.s1%p*2%p*j%p*(power%p)%p+y.s2,p);\n                    }\n                }\n            }\n        }\n    }\n    //è¿éå¤çä¸ºäºæ¹ä¾¿ä»¥åéä½æ¶é´å¤æåº¦ã\n    power7[0]=1,power9[0]=1;\n    for(int i=1;i<n;i++){\n        power7[i]=power7[i-1]*10%7;\n        power9[i]=power9[i-1]*10%p;\n    }\n}\nf get(int i,int j,int k,int u){\n    //å ä¸ºf[i][j][k][u]æ¯æ¬èº«æ¨¡7ç­äºkï¼ä¸åä½æ°ä¹å